pragma solidity >0.4.99 <0.6.0;

contract Mail {

  struct mailContent{
    address sender;
    string content;
  }

  mailContent[] public mailBox;
  mapping(address => uint[]) receiveMailIdxList;
  mapping (address => uint[]) sendMailIdxList;

  function sendMail(address _receiver, string memory _content) public{
    uint idx = mailBox.length;
    sendMailIdxList[msg.sender].push(idx);
    receiveMailIdxList[_receiver].push(idx);
    mailBox.push(mailContent(msg.sender, _content));
  }

  function getReceiveMailBox() external view returns (uint[] memory){
    return receiveMailIdxList[msg.sender];
  }

  function getMailContent(uint _idx) external view returns (address, string memory){
    return (mailBox[_idx].sender, mailBox[_idx].content);
  }


}
